$(document).ready(function() {
    $(".popup").hide(), $('[data-popup="btn"]').click(function() {
        $(".popup").fadeIn();
    }), $(".popup").click(function(t) {
        $(t.target).hasClass("popup") && $(this).fadeOut();
    }), $(".menu__item").click(function(t) {
        t.preventDefault(), $("html, body").animate({
            scrollTop: $($(this).attr("href")).offset().top - 100
        }, "slow");
    }), $(".section__btn").click(function(t) {
        $("html, body").animate({
            scrollTop: $($(this).attr("data-anchor")).offset().top - 100
        }, "slow");
    }), $(".partners-slider").owlCarousel({
        loop: !0,
        nav: !1,
        items: 1
    }), $(".gallery-slider").owlCarousel({
        loop: !0,
        nav: !1,
        items: 5,
        center: !0,
        margin: 30,
        autoWidth: !0
    }), $(".slider-btn-right").click(function() {
        $("." + $(this).attr("data-for")).trigger("next.owl.carousel");
    }), $(".slider-btn-left").click(function() {
        $("." + $(this).attr("data-for")).trigger("prev.owl.carousel");
    });
});